import React, {Component} from "react";
import ReactDOM from "react-dom";
import "bootstrap/dist/css/bootstrap.css"
import "./index.scss"
import {quotes, mdColors} from "./constents";
import { faCoffee } from '@fortawesome/free-solid-svg-icons'




class Quote extends Component{
    constructor(props){
        super(props);
        this.state={
            quote:"Be yourself; everyone else is already taken.",
            author:"Oscar Wilde",
            color : '#F44336'
        };
        this.handleClick =this.handleClick.bind(this);
        
    }
    handleClick(){
        let randomIndex = Math.floor(Math.random()*quotes.length);
        this.setState({
            quote: quotes[randomIndex].quote,
            author: quotes[randomIndex].author,
            color: mdColors[randomIndex]
        });
    }
    
    render(){
        return(
            <div className="d-flex align-items-center justify-content-center height" style={{backgroundColor: this.state.color}}>
                <div className="box p-3 rounded">
                    <h3 style={{color: this.state.color}}>{this.state.quote}</h3>
                    <p style={{color: this.state.color}} className="author">- {this.state.author}</p>
                    <p>
                        <a style={{color: this.state.color}} href="">twitter icon</a>
                        <a style={{color: this.state.color}} href="">Youtube icon</a>
                        <button onClick={this.handleClick} className="btn btn-default" style={{backgroundColor: this.state.color}}>New quote</button>
                    </p>
                </div>
            </div>
        );
    }
}

ReactDOM.render(<Quote/>, document.getElementById("root"))
