import React, {Component} from "react";
import ReactDOM from "react-dom";
import "bootstrap/dist/css/bootstrap.css"
import "./index.scss"
import {quotes} from "./constents";




class Quote extends Component{
    constructor(props){
        super(props);
        this.state={
            quote:"Be yourself; everyone else is already taken.",
            author:"Oscar Wilde"
        };
        this.handleClick =this.handleClick.bind(this);
        
    }
    handleClick(){
        let randomIndex = Math.floor(Math.random()*quotes.length);
        this.setState({
            quote: quotes[randomIndex].quote,
            author: quotes[randomIndex].author
        })
    }
    render(){
        return(
            <div className="d-flex align-items-center justify-content-center height">
                <div className="box p-3 rounded">
                    <h3>{this.state.quote}</h3>
                    <p className="author">- {this.state.author}</p>
                    <p>
                        <a href="">twitter icon</a>
                        <a href="">yOUTUBE icon</a>
                        <button onClick={this.handleClick} className="btn btn-default">New quote</button>
                    </p>
                </div>
            </div>
        );
    }
}

ReactDOM.render(<Quote/>, document.getElementById("root"))
